mapper.select:
select pb.branch_num, pc.category_num, pc.category_name,
pi.item_num, pi.item_name, pi.item_image, pi.item_info, pi.item_lastdate, pi.item_regidate
from phosk_branch pb INNER JOIN phosk_category pc on (pb.branch_num = pc.branch_num)
INNER JOIN phosk_item pi on(pc.category_num = pi.category_num)
where pb.branch_num = #{branch_num} and pc.category_num =
#{category_num} and pi.item_num = #{item_num}

select pi.branch_num, pi.category_num, pi.item_num, pi.item_name, pi.item_image, pi.item_info, bo.basic_option, bo.basic_price from phosk_item pi INNER JOIN phosk_basicoption bo ON pi.item_num = bo.item_num
 WHERE pi.branch_num = #{branch_num}


 		and pi.category_num = #{category_num}


 		and pi.category_num = 0

select basic_option, basic_price 
from phosk_basicoption where branch_num = #{branch_num} and
category_num = #{category_num} and item_num = #{item_num}

select add_option, add_price 
from phosk_addoption where branch_num = #{branch_num} and
category_num = #{category_num} and item_num = #{item_num}

mapper.insert:
insert into phosk_item VALUES(#{branch_num}, #{category_num}, #{category_name}, #{item_num}, #{item_name}


		, #{item_image}


		, null




		, #{item_info}


		, null


, sysdate, sysdate)

mapper.update:
update phosk_category set
category_name = #{category_name}
where branch_num = #{branch_num} and category_num = #{category_num}

update phosk_item set
category_num = #{change_category_num},
item_name = #{item_name}


		, item_image = #{item_image}


		, item_info = #{item_info}

	, item_lastDate = sysdate where branch_num = #{branch_num} and category_num = #{category_num} and item_num = #{item_num}

update.if:
update phosk_addoption set
category_num = #{category_num}, add_option = #{change_add_option}, add_price = #{add_price}
where branch_num = #{branch_num} and item_num = #{item_num} and add_option = #{add_option}
